import './SideBarComponent.css';
import React, { useState } from 'react';
import Collapse from 'react-bootstrap/Collapse';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.min.js';

import CohortProjects from './CohortProjects.js';

function SideBarComponent(prop) {

  const [isVisible, setvisibility] = useState(false)
  const invokeCollapse = () => {
    console.log("Clicked")
    setvisibility(!isVisible)
    if (isVisible){
      CohortProjList_class="WhiteClass";
    }
    else{
      CohortProjList_class="CurrentClass";
    }
    return isVisible
  }

    return (
          <div class={CohortProjList_class}>
            <div onClick={invokeCollapse}>
            {prop.cohortResult.cohortName.value}
            </div>

            <Collapse in={isVisible} className="">
              <div>

                <CohortProjects data={prop.cohortResult.cohortName.value}/>
              </div>
          </Collapse>
            
          </div>
        );
    }



  
export default SideBarComponent;
